import React, { useState, useEffect } from 'react';
import './navbar.css';
import linkedinIcon from "../../assets/images/lnl.png";
import githubIcon from "../../assets/images/github.png";
import resumeIcon from "../../assets/images/docs.png";
import contactIcon from "../../assets/images/mb1.png";
import logo from "../../assets/images/logo.png";
import resume from "../../assets/images/PloynapaYang(Resume)-2025.pdf";

function Navbar() {
  const [activeLink, setActiveLink] = useState('');

  // 
  useEffect(() => {
    const handleScroll = () => {
      const sections = ['home', 'aboutme', 'projects', 'ploybot']; // เปลี่ยน chat เป็น ploybot
      let currentSection = '';

      for (let section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= 150 && rect.bottom >= 150) {
            currentSection = `#${section}`;
            break;
          }
        }
      }
      setActiveLink(currentSection);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <nav className="navbar">
      {/* Left Navbar */}
      <div className="navbar-right">
        <p>
          {['home', 'aboutme', 'projects', 'ploybot'].map((section) => ( 
            <a
              key={section}
              href={`#${section}`}
              className={activeLink === `#${section}` ? 'active' : ''}
            >
              {section === 'ploybot' ? 'PloyBot' : section.charAt(0).toUpperCase() + section.slice(1)}
            </a>
          ))}
        </p>
      </div>

      {/* Middle Logo */}
      <div className="navbar-mid">
        <img src={logo} alt="Logo" style={{ borderRadius: '50%' }} />
      </div>

      {/* Right Navbar */}
      <div className="navbar-left">
        <a href="https://www.linkedin.com/in/ploynapa-py/" target="_blank" rel="noopener noreferrer" className="tooltip-container">
          <img src={linkedinIcon} alt="LinkedIn" width={40} />
          <span className="tooltip">LinkedIn</span>
        </a>
        <a href="https://github.com/Ploynpk" target="_blank" rel="noopener noreferrer" className="tooltip-container">
          <img src={githubIcon} alt="GitHub" width={40} />
          <span className="tooltip">GitHub</span>
        </a>
        <a href={resume} rel="noopener noreferrer" className="tooltip-container">
          <img src={resumeIcon} alt="Resume" width={38} />
          <span className="tooltip">Resume</span>
        </a>
        <a href="mailto:ploy@ploynapa.com" rel="noopener noreferrer" className="tooltip-container">
          <img src={contactIcon} alt="Contact Me" id="github1" />
          <span className="tooltip">Contact Me</span>
        </a>
      </div>
    </nav>
  );
}

export default Navbar;
