import React from 'react';
import './intro.css';


function Intro() {
  return (
    <div className="intro-container">
      <div className="intro-left">
        {/* Profile Image */}
        <img src="/assests/me.png" alt="Your Name" className="profile-image" />
      </div>

      <div className="intro-right">
        <h1 className="intro-top">
        Transforming Ideas Into Impactful Solutions Through Code and Design.
        </h1>
        <p className="intro-summary">
        As a passionate full-stack developer, I specialize in bringing creative ideas to life through cutting-edge web applications. Dive into my recent work, highlighting my proficiency in React.js and full-stack development.
        </p>

        {/* Resume Button */}
        <a href="assests/PloynapaYang(Resume)-2025.pdf" className="resume">
          <button>Resume
          <img src="/assests/a.png" alt="Arrow Icon" className="resume-arrow" />
          </button>
        </a>

        {/* Contact Link */}
        <a href="mailto:ploy@ploynapa.com" className="contact">Contact</a>
      </div>
    </div>
  );
}

export default Intro;
