import React , {useEffect}from 'react';
import Navbar from './components/Navbar/Navbar.jsx';
import HomePage from './components/Home/Home.jsx';
import AboutMe from './components/AboutMe/AboutMe.jsx';
import Projects from './components/Projects/Projects.jsx';
import ChatPage from './components/ChatAI/Chat.jsx';
import './App.css';
import { BrowserRouter, Routes, Route, useLocation } from 'react-router-dom';

function App() {

  const location = useLocation(); // ใช้เพื่อดึงเส้นทาง URL ปัจจุบัน

  useEffect(() => {
    // เปลี่ยนสีพื้นหลังตาม path
    switch (location.pathname) {
      case '/home':
        document.body.style.backgroundColor = '#FFEB3B'; // สีพื้นหลัง Home
        break;
      case '/aboutme':
        document.body.style.backgroundColor = '#8BC34A'; // สีพื้นหลัง About Me
        break;
      case '/projects':
        document.body.style.backgroundColor = '#03A9F4'; // สีพื้นหลัง Projects
        break;
      case '/chatwithploy':
        document.body.style.backgroundColor = '#FFC107'; // สีพื้นหลัง Chat
        break;
      default:
        document.body.style.backgroundColor = '#FFFFFF'; // สีพื้นหลังเริ่มต้น
    }
  }, [location]);

  return (
    <BrowserRouter>
     <nav className="navbar">
      <Navbar />
      </nav>

      <div className="app">
        <div className="background-row">
          <img src="/assests/bc2.png" alt="bmonkey" id="mon1" />
          <img src="/assests/bc3.png" alt="monkey" id="mon2" />
          <img src="/assests/bcc.png" alt="monkey" id="mon3" />
          <img src="/assests/bcc2.png" alt="monkey" id="mon4" />
        </div>

        {/* Sections with unique ids for scrolling */}
        <div id="home" className="section">
          <HomePage />
        </div>

        <div id="aboutme" className="section">
          <AboutMe />
        </div>

        <div id="projects" className="section">
          <Projects />
        </div>

        <div id="chat" className="section">
          <ChatPage />
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
