import React, { useState, useEffect } from 'react';
import './navbar.css';

function Navbar() {
  const [activeLink, setActiveLink] = useState('');
  const [textColor, setTextColor] = useState('#000000'); // เริ่มต้นด้วยสีดำ

  useEffect(() => {
    function getTextColor(backgroundColor) {
      const r = parseInt(backgroundColor.slice(1, 3), 16);
      const g = parseInt(backgroundColor.slice(3, 5), 16);
      const b = parseInt(backgroundColor.slice(5, 7), 16);
      const luminance = (0.299 * r + 0.587 * g + 0.114 * b) / 255;
      return luminance > 0.5 ? '#000000' : '#FFFFFF';
    }

    function updateNavbarTextColor() {
      const bodyBackgroundColor = window.getComputedStyle(document.body).backgroundColor;
      const hex = '#' + bodyBackgroundColor.match(/\d+/g).map(x => parseInt(x).toString(16).padStart(2, '0')).join('');
      setTextColor(getTextColor(hex));
    }

    window.addEventListener('load', updateNavbarTextColor);
    window.addEventListener('scroll', updateNavbarTextColor);

    // Cleanup
    return () => {
      window.removeEventListener('load', updateNavbarTextColor);
      window.removeEventListener('scroll', updateNavbarTextColor);
    };
  }, []);

  const handleLinkClick = (e, section) => {
    setActiveLink(section);
  };

  return (
    <nav className="navbar" style={{ color: textColor }}>
      {/* ส่วนที่เหลือของ Navbar component */}
      <div className="navbar-right">
        <p>
          <a
            href="#home"
            className={activeLink === '#home' ? 'active' : ''}
            onClick={(e) => handleLinkClick(e, '#home')}
            style={{ color: textColor }}
          >
            Home
          </a>
          {/* ทำแบบเดียวกันกับลิงก์อื่นๆ */}
        </p>
      </div>
      {/* ... */}
    </nav>
  );
}

export default Navbar;
