import React, { useEffect, useRef } from 'react';
import './aboutme.css';
import AWS from '../../assets/images/AWS.svg';
import Cloudflare from '../../assets/images/Cloudflare.svg';
import RasberryPi from '../../assets/images/RasberryPi.svg';
import HTML5 from '../../assets/images/HTML5.svg';
import CSS from '../../assets/images/CSS.svg';
import JavaScript from '../../assets/images/JavaScript.svg';
import TypeScript from '../../assets/images/TypeScript.svg';
import Python from '../../assets/images/Python.svg';
import ReactLogo from '../../assets/images/React.png';
import MaterialUI from '../../assets/images/MaterialUI.svg';
import Git from '../../assets/images/Git.svg';
import GitHub from '../../assets/images/GitHub.svg';
import JQuery from '../../assets/images/JQuery.svg';
import NodeJS from '../../assets/images/NodeJS.svg';
import Express from '../../assets/images/Express.svg';
import MongoDB from '../../assets/images/MongoDB.svg';
import PostgreSQL from '../../assets/images/PostgreSQL.png';
import Docker from '../../assets/images/Docker.png';
import Websocket from '../../assets/images/Websocket.png';
import RabbitMQ from '../../assets/images/RabbitMQ.svg';
import CICD from '../../assets/images/CICD.png';
import Redis from '../../assets/images/Redis.svg';
import Redux from '../../assets/images/Redux.svg';
import SQLite from '../../assets/images/SQLite.svg';
import Postman from '../../assets/images/Postman.png';
import Supabase from '../../assets/images/Supabase.svg';
import Jest from '../../assets/images/Jest.svg';
import Jira from '../../assets/images/Jira.png';
import Vite from '../../assets/images/Vite.svg';
import Webpack from '../../assets/images/Webpack.svg';
import PloyPic from '../../assets/images/ploy4.png';



function AboutMe() {
  const techSkills = [
    AWS, Cloudflare, RasberryPi, HTML5, CSS, JavaScript, TypeScript, Python, 
    ReactLogo, MaterialUI, Git, GitHub, JQuery, NodeJS, Express, MongoDB, 
    PostgreSQL, Docker, Websocket, RabbitMQ, CICD, Redis, Redux, SQLite, 
    Postman, Supabase, Jest, Jira, Vite, Webpack
  ];

  const experiencesRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            // Add the "animate" class to all experience items
            const items = entry.target.querySelectorAll('.experience-item');
            items.forEach((item, index) => {
              setTimeout(() => item.classList.add('animate'), index * 200); 
            });
          }
        });
      },
      { threshold: 0.2 }, // Trigger when at least 20% of the section is visible
    );

    const experiences = experiencesRef.current;
    if (experiences) observer.observe(experiences);

    return () => {
      if (experiences) observer.unobserve(experiences);
    };
  }, []);

  return (
    <div>
      <div className="aboutme-wrapper">
        {/* Left intro */}
        <img src={PloyPic} alt="ploypic" className="mypic" />

        {/* About me */}
        <div className="aboutme-container">
          <div className="aboutme-header">
            <h1>I am Ploy.</h1>
          </div>
          <h2>
          <span id='i'>I</span> am a software engineer passionate about creating seamless experiences across the entire development stack. <br /><span id='i'>I</span> enjoy solving complex problems and building high-quality, user-centric solutions.
        </h2>
          <p>
            Outside of work, I enjoy outdoor runs as a form of meditation,
            experimenting with Thai-Italian fusion cuisine in my kitchen, and
            tinkering with AI-enhanced home automation systems. When winter
            comes, you’ll find me shredding the slopes on my snowboard.
          </p>
          <p>
            <br />
            Growing up in Thailand taught me the value of adaptability and
            collaboration, which I bring to every team I work with.
          </p>
          <p style={{ fontStyle: 'italic'}}>
            <br />
            "I am a hard worker who stays determined and persistent until I
            achieve my goals."
          </p>
        </div>
      </div>

       {/* Experiences Section */}
       <div className="experiences" ref={experiencesRef}>
          <h1 id="ex-header">MY EXPERIENCES.</h1>
          <div className="experience-item current">
            <div>
              <h1>OSLabs | DeClustor</h1>
              <p>Software Engineer</p>
            </div>
            <h1 className="year">2024~</h1>
          </div>
          <div class='experiences-detail current'>
            <ul>
                <li>
                Leverage <span style={{ fontWeight: 'bold'}}>React</span>’s custom hooks to streamline state management and minimize re-renders, accelerating the dashboard's update cycle by 35% and significantly enhancing data visualization responsiveness across multiple ECS clusters.
                </li>
                <li>
                Centralize global state management across 17 AWS regions using <span style={{ fontWeight: 'bold'}}>Redux Toolkit</span>, optimizing slices and selectors to enhance data consistency and scalability in complex, lowering data-fetch latency by 15% in multi-account environments.
                </li>
                <li>
                Design a data analytics dashboard with <span style={{ fontWeight: 'bold'}}>Material UI and CSS</span>, implementing reusable components and a global theme to standardize styling and improve code reusability, reducing new feature implementation time by 20%.
                </li>
                <li>
                Enhance system monitoring by implementing <span style={{ fontWeight: 'bold'}}>Nivo and D3.js </span>SVG charts for CPU, memory, and network metrics with 30-second refresh rates, facilitating faster analysis and cutting time to diagnose and address performance issues by 25%.
                </li>
                <li>
                Implement <span style={{ fontWeight: 'bold'}}>WebSocket</span> to deliver real-time ECS performance anomaly notifications, leveraging their bi-directional communication capability to eliminate polling and reduce detection latency by 40%, enabling faster incident response.
                </li>
                <li>
                Engineer a <span style={{ fontWeight: 'bold'}}>Node.js/Express.js</span> backend with <span style={{ fontWeight: 'bold'}}>AWS SDK</span> integration to securely manage credentials and configure custom alerts via secure REST APIs, reducing monitoring response time by 30% and empowering precise user control.
                </li>
                <li>
                Strengthened application security by containerizing services with <span style={{ fontWeight: 'bold'}}>Docker</span>, utilizing its isolation capabilities to safeguard sensitive user data and significantly reduce the risk of unauthorized access.
                </li>
                <li>
                Orchestrated an automated <span style={{ fontWeight: 'bold'}}>CI/CD pipeline with GitHub Actions</span>, achieving 90% test coverage to ensure high code quality and minimize production errors.
                </li>
                <li>
                Coordinate 50+ agile sprints using <span style={{ fontWeight: 'bold'}}>Jira</span>, enhancing team collaboration and consistently delivering MVPs on time.
                </li>
              </ul>
          </div>


          <div className="experience-item">
            <div>
              <h1>Petder Better</h1>
              <p>Software Engineer</p>
            </div>
            <h1 className="year">2024</h1>
          </div>
          <div class='experiences-detail'>
            <ul>
              <li>
              Boosted data reliability and consistency by architecting a <span style={{ fontWeight: 'bold'}}>PostgreSQL</span> database using ACID-compliant transactions, resulting in a 15% increase in user retention through improved data accuracy and enhanced system credibility.
              </li>
              <li>
              Achieved 85% test coverage across backend and frontend by implementing <span style={{ fontWeight: 'bold'}}>Jest and React Testing Library</span>, focusing on TDD principles to strategically prevent regressions and reduce UI interaction bugs by 10% post-deployment.
              </li>
              <li>
              Configured secure client-side routing in React using <span style={{ fontWeight: 'bold'}}>React Router</span> to restrict access to protected routes using role-based access, ensuring only authenticated users can manage their data.
              </li>
            </ul>
         </div>

          <div className="experience-item">
            <div>
              <h1>LunchBox</h1>
              <p>Software Engineer</p>
            </div>
            <h1 className="year">2024</h1>
          </div>
          <div class='experiences-detail'>
            <ul>
              <li>
              Capitalized <span style={{ fontWeight: 'bold'}}>MongoDB</span>'s flexible schema and indexing capabilities to optimize data retrieval for 10,000+ recipes, cutting query response times by 20% and enhancing user experience with faster access to nutritional information.              </li>
              <li>
              Integrated third-party recipe APIs into a <span style={{ fontWeight: 'bold'}}>Node.js/Express</span> backend, optimizing custom middleware for efficient data retrieval, user authentication, and robust error handling to manage over 500 daily requests smoothly.              </li>
            </ul>
         </div>


          <div className="experience-item">
            <div>
              <h1>Jumpa Co-op</h1>
              <p>Shift Lead/Hospitality Specialist</p>
            </div>
            <h1 className="year">2018-2023</h1>
          </div>

          <div className="experience-item">
            <div>
              <h1>Jumpa Co-op</h1>
              <p>Product Manager (Intern)</p>
            </div>
            <h1 className="year">2017-2018</h1>
          </div>

          <div className="experience-item">
            <div>
              <h1>Mitr Phol Group</h1>
              <p>Export and Logistic Operator</p>
            </div>
            <h1 className="year">2013-2015</h1>
          </div>

          <div className="experience-item">
            <div>
              <h1>Mitr Phol Group</h1>
              <p>Procurement Officer (Intern)</p>
            </div>
            <h1 className="year">2012-2013</h1>
          </div>
        </div>


      {/* Tech skills */}
      <div className="tech-container">
        <h1>MY SKILLS.</h1>
        <div className="tech-slider">
          {[...techSkills, ...techSkills].map((src, index) => {
            const techName = src.split('/').pop().split('.')[0];
            return (
              <div key={index} className="tooltip-container">
                <img src={src} alt={techName} />
                <span className="tooltip">{techName}</span>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default AboutMe;
