import React, { useState } from 'react';
import './project.css';

const projectDetails = {
  PayPro: "A secure payment gateway system.",
  "Petder Better": "A pet matching app like Tinder for pets.",
  LunchBox: "A meal prep management app.",
  DeClustor: "A cloud monitoring tool for AWS ECS.",
  JigglyDex: "A Pokémon card collection and battle platform.",
  NextFlick: "A movie recommendation system.",
  "Lucky Dog": "An adoption platform for rescue dogs."
};

function Projects() {
  const [selectedProject, setSelectedProject] = useState("DeClustor");

  return (
    <div className='projects-container'>
      {/* กล่องแสดงรายละเอียด */}
      <div className={`project-detail ${selectedProject ? "active-project" : ""}`}>
        <h2>{selectedProject}</h2>
        <p>{projectDetails[selectedProject]}</p>
      </div>

      {/* รายการโปรเจคด้านขวา */}
      <div className='project-list'>
        <h1>PROJECT LISTS</h1>
        <ul>
          {Object.keys(projectDetails).map((project) => (
            <li 
              key={project} 
              onClick={() => setSelectedProject(project)}
              className={selectedProject === project ? "active" : ""}
            >
              {project}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Projects;
