import React, { useState } from 'react';
import './aboutme.css';

function AboutMe() {
  const [activeExperience, setActiveExperience] = useState('current'); // Default to showing the current experience

  const handleExperienceClick = (experienceKey) => {
    setActiveExperience(experienceKey); // Update the active experience based on the clicked item
  };

  return (
    <div>
      <div className="aboutme-wrapper">
        {/* About Me Section */}
        {/* Your existing "About Me" code remains the same */}
      </div>

      {/* Experiences Section */}
      <div className="experiences">
        <h1 id="ex-header">MY EXPERIENCES.</h1>

        <div
          className="experience-item"
          onClick={() => handleExperienceClick('declustor')}
        >
          <div>
            <h1>OSLabs | DeClustor</h1>
            <p>Software Engineer</p>
          </div>
          <h1 className="year">2024~</h1>
        </div>
        <div
          className={`experiences-detail ${
            activeExperience === 'declustor' ? 'current' : 'hidden'
          }`}
        >
          <ul>
            {/* Add specific details for DeClustor */}
            <li>Leverage React's custom hooks...</li>
          </ul>
        </div>

        <div
          className="experience-item"
          onClick={() => handleExperienceClick('petder')}
        >
          <div>
            <h1>Petder Better</h1>
            <p>Software Engineer</p>
          </div>
          <h1 className="year">2024</h1>
        </div>
        <div
          className={`experiences-detail ${
            activeExperience === 'petder' ? 'current' : 'hidden'
          }`}
        >
          <ul>
            {/* Add specific details for Petder Better */}
            <li>Boosted data reliability...</li>
          </ul>
        </div>

        <div
          className="experience-item"
          onClick={() => handleExperienceClick('lunchbox')}
        >
          <div>
            <h1>LunchBox</h1>
            <p>Software Engineer</p>
          </div>
          <h1 className="year">2024</h1>
        </div>
        <div
          className={`experiences-detail ${
            activeExperience === 'lunchbox' ? 'current' : 'hidden'
          }`}
        >
          <ul>
            {/* Add specific details for LunchBox */}
            <li>Capitalized MongoDB's flexible schema...</li>
          </ul>
        </div>

        {/* Add more items as needed */}
      </div>
    </div>
  );
}

export default AboutMe;
